mapper.select:
select distinct id
from breeding_plan
where customer_id = #{customerId,jdbcType=INTEGER}

select bp.batch_no batchNo,bp.customer_id customerId, po.purchase_name purchaseName, po.id purchaseOrderId,po.purchase_no purchaseNo,po.product_type productType,po.purchase_order_status purchaseOrderStatus, po.plan_execute_time planExecuteTime,po.plan_delivery_time planDeliveryTime ,po.signer_id signerId from breeding_plan bp inner join purchase_order po on bp.id=po.plan_id
1=1
and bp.customer_id IN
${customerId}
and po.purchase_order_status=#{purchaseOrderStatus}
and bp.create_user_name like concat('%', #{createUserName},'%')
