mapper.select:
SELECT
    count(breeding_value) as breeding_value
FROM
    breeding_record
WHERE
    enable = 1
    AND DATE_FORMAT( create_time, '%Y-%m-%d' ) = #{createTime}
    AND coop_id = #{coopId}
    AND plan_id = #{planId}
    AND record_type = 1

SELECT
    sum(breeding_value) as breeding_value
FROM
    breeding_record
WHERE
    enable = 1
    AND DATE_FORMAT( create_time, '%Y-%m-%d' ) = #{createTime}
    AND coop_id = #{coopId}
    AND plan_id = #{planId}
    AND record_type = 4
