mapper.select:
select sum(weight) totalPlanFeedWeight from purchase_order
enable=1
and product_type=#{productType}
and plan_id in
${planId}

select distinct o.id from purchase_order o, purchase_order_items i
where o.id=i.purchase_order_id
      and i.product_id=#{productId}
      and o.product_type = #{productType}
      and o.plan_id=#{planId} and o.order_phase=#{orderPhase}

mapper.delete:
UPDATE purchase_order_items
SET ENABLE = 0
where
  purchase_order_id in
      (select id from purchase_order where plan_id = #{planId}
          AND order_phase = #{orderPhase}
          AND product_type = #{productType}
      );
UPDATE purchase_order
SET ENABLE = 0
WHERE
    plan_id = #{planId}
AND order_phase = #{orderPhase}
AND product_type = #{productType}
